    def dropEvent(self, event):
        if(event.mimeData().hasImage):
            event.setDropAction(QtCore.Qt.CopyAction)
            file_path = event.mimeData().urls()[0].toLocalFile()
            self.Label_Bild.setPixmap(QtGui.QPixmap(file_path))

            event.accept()
        else:
            event.ignore()

